    public static List<AddressBean> getRootDepart(){
        List<AddressBean> departs = DataSupport.select("nodeGuid","nodeName","parentNodeGuid","imageUrl").where("imageUrl like ?", "%p%").order("index").find(AddressBean.class);
        List<AddressBean> roots=new ArrayList<>();
        for (int i = 0; i < departs.size(); i++) {
            if (isRoot(departs.get(i).getParentNodeGuid())) {
                roots.add(departs.get(i));
            }
        }
        return roots;
    }

    public static List<AddressBean> getDepart(String id){
        List<AddressBean> departs = DataSupport.select("nodeGuid","nodeName","parentNodeGuid","imageUrl","index").where("nodeGuid = ?", id).find(AddressBean.class);
        List<AddressBean> roots=new ArrayList<>();
        for (int i = 0; i < departs.size(); i++) {
            if (isRoot(departs.get(i).getParentNodeGuid())) {
                roots.add(departs.get(i));
            }
        }
        return roots;
    }

    public static boolean isRoot(String departId){
        List<AddressBean> persons = DataSupport.select("nodeGuid","nodeName").where("nodeGuid = ?", departId).find(AddressBean.class);
        if (persons.size()>0) {
            return false;
        }
        return true;
    }
